const data = require('../data/data');

const getItems = (req, res) => {
    const { id } = req.params;
    if (id) {
        if (data[id]) {
            res.json(data[id]);
        } else {
            res.status(404).json({ message: 'Item not found' });
        }
    } else {
        res.json(Object.values(data));
    }
};

module.exports = { getItems };

