const updateItem = (req, res) => {
    const { id } = req.params;
    if (!data[id]) {
        return res.status(404).json({ message: 'Item not found' });
    }
    data[id] = {
        ...data[id],
        ...req.body
    };
    res.json(data[id]);
};

module.exports = { getItems, createItem, updateItem };

